macros.command:
this.setupVirtualItems();
const values = this.item.getFlag("sohl", "values");
this.actor.system.$ssMod = values[this.textValue];

this.setupVirtualItems();
this.$masteryLevel.disabled = false;
this.$masteryLevel.setBase(this.$score.base);
const aura = this.actor.getTraitByAbbrev("aur");
if (aura) {
    this.$masteryLevel.add("Aura Secondary Modifier", "AuraSM", aura.system.$masteryLevel.secMod);
}
const ss = this.actor.getTraitByAbbrev("ss");
if (ss) {
    const values = ss.getFlag("sohl", "values");
    const value = values[this.textValue];
    if (value) {
        this.$masteryLevel.add("Sunsign Modifier", "SS", value["aura"] || 0);
    }
}

this.setupVirtualItems();
const sizes = this.item.getFlag("sohl", "sizes");
this.$params = sizes?.[this.textValue] || {
    maxStr: 19,
    lbs: 400,
    zones: 10,
    target: 1,
    reachMod: 0,
    spaces: 1,
    feet: 5
};

this.processSiblings();
const gw = this.actor.system.$gearWeight;
gw.maxFight.setBase(this.$score.base);
gw.max.setBase(this.max);
